function typeText(elementId, text, delay) {
  let index = 0;
  function type() {
    if (index <= text.length) {
      document.getElementById(elementId).textContent = text.substring(0, index);
      index++;
      setTimeout(type, 100);
    } else {
      setTimeout(() => {
        document.getElementById("detail0").setAttribute("opacity", "1");
        document.getElementById("detail1").setAttribute("opacity", "1");
        document.getElementById("detail2").setAttribute("opacity", "1");
        document.getElementById("detail3").setAttribute("opacity", "1");
        document.getElementById("detail4").setAttribute("opacity", "1");
        document.getElementById("detail5").setAttribute("opacity", "1");
        document.getElementById("detail6").setAttribute("opacity", "1");
      }, 500);
    }
  }
  setTimeout(type, delay);
}

document.addEventListener("DOMContentLoaded", function () {
  typeText("command", "whoami", 500);
});
